  const ops = ["/", "*", "+", "-", "."];
  const updateCalc = value => {
    setCalc(calc + value);
  }
  const createDigits = () => {
    const digits = [];

    for (let i = 1; i < 10; i++) {
      digits.push(<button key={i}>{i}</button>);
    }
    return digits;
  };
  return (
    <div className="App">
      <div className="calculator">
        <div className="display">
          <span>(0)</span>{ca}
        </div>
        <div className="operators">
          <button>/</button>
